mapper.delete:
DELETE FROM tbl_catch_pal
WHERE USER_ID = #{ userId }
    AND PAL_NO = #{ palNo }
LIMIT 1

mapper.select:
SELECT chance
FROM TBL_PAL_GRADE
WHERE PAL_GRADE = #{ palGrade }

SELECT COUNT(*)
FROM TBL_CATCH_PAL
WHERE PAL_NO = #{ palNo }
AND USER_ID = #{ userId }

mapper.update:
UPDATE TBL_PALDEX_BOOK SET
CATCH_COUNT = #{ catchCount } + 1
WHERE
USER_ID = #{ userId }
AND PAL_NO = #{ palNo }
